const COORDINATOR_TYPES = require('kafkajs/src/protocol/coordinatorTypes')

const versions = {
  0: ({ groupId }) => {
    const request = require('kafkajs/src/protocol/requests/findCoordinator/v0/request')
    const response = require('kafkajs/src/protocol/requests/findCoordinator/v0/response')
    return { request: request({ groupId }), response }
  },
  1: ({ groupId, coordinatorType = COORDINATOR_TYPES.GROUP }) => {
    const request = require('kafkajs/src/protocol/requests/findCoordinator/v1/request')
    const response = require('kafkajs/src/protocol/requests/findCoordinator/v1/response')
    return { request: request({ coordinatorKey: groupId, coordinatorType }), response }
  },
  2: ({ groupId, coordinatorType = COORDINATOR_TYPES.GROUP }) => {
    const request = require('kafkajs/src/protocol/requests/findCoordinator/v2/request')
    const response = require('kafkajs/src/protocol/requests/findCoordinator/v2/response')
    return { request: request({ coordinatorKey: groupId, coordinatorType }), response }
  },
}

module.exports = {
  versions: Object.keys(versions),
  protocol: ({ version }) => versions[version],
}
