const versions = {
  0: ({ groupId, memberId }) => {
    const request = require('kafkajs/src/protocol/requests/leaveGroup/v0/request')
    const response = require('kafkajs/src/protocol/requests/leaveGroup/v0/response')
    return {
      request: request({ groupId, memberId }),
      response,
    }
  },
  1: ({ groupId, memberId }) => {
    const request = require('kafkajs/src/protocol/requests/leaveGroup/v1/request')
    const response = require('kafkajs/src/protocol/requests/leaveGroup/v1/response')
    return {
      request: request({ groupId, memberId }),
      response,
    }
  },
  2: ({ groupId, memberId }) => {
    const request = require('kafkajs/src/protocol/requests/leaveGroup/v2/request')
    const response = require('kafkajs/src/protocol/requests/leaveGroup/v2/response')
    return {
      request: request({ groupId, memberId }),
      response,
    }
  },
  3: ({ groupId, memberId, groupInstanceId }) => {
    const request = require('kafkajs/src/protocol/requests/leaveGroup/v3/request')
    const response = require('kafkajs/src/protocol/requests/leaveGroup/v3/response')
    return {
      request: request({ groupId, members: [{ memberId, groupInstanceId }] }),
      response,
    }
  },
}

module.exports = {
  versions: Object.keys(versions),
  protocol: ({ version }) => versions[version],
}
