const ISOLATION_LEVEL = require('kafkajs/src/protocol/isolationLevel')

// For normal consumers, use -1
const REPLICA_ID = -1

const versions = {
  0: ({ replicaId = REPLICA_ID, topics }) => {
    const request = require('kafkajs/src/protocol/requests/listOffsets/v0/request')
    const response = require('kafkajs/src/protocol/requests/listOffsets/v0/response')
    return { request: request({ replicaId, topics }), response }
  },
  1: ({ replicaId = REPLICA_ID, topics }) => {
    const request = require('kafkajs/src/protocol/requests/listOffsets/v1/request')
    const response = require('kafkajs/src/protocol/requests/listOffsets/v1/response')
    return { request: request({ replicaId, topics }), response }
  },
  2: ({ replicaId = REPLICA_ID, isolationLevel = ISOLATION_LEVEL.READ_COMMITTED, topics }) => {
    const request = require('kafkajs/src/protocol/requests/listOffsets/v2/request')
    const response = require('kafkajs/src/protocol/requests/listOffsets/v2/response')
    return { request: request({ replicaId, isolationLevel, topics }), response }
  },
  3: ({ replicaId = REPLICA_ID, isolationLevel = ISOLATION_LEVEL.READ_COMMITTED, topics }) => {
    const request = require('kafkajs/src/protocol/requests/listOffsets/v3/request')
    const response = require('kafkajs/src/protocol/requests/listOffsets/v3/response')
    return { request: request({ replicaId, isolationLevel, topics }), response }
  },
}

module.exports = {
  versions: Object.keys(versions),
  protocol: ({ version }) => versions[version],
}
